import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setPosts, deletePost } from "state";
import PostWidget from "./PostWidget";

const PostsWidget = ({ userId, isProfile = false }) => {
  const dispatch = useDispatch();
  const posts = useSelector((state) => state.posts);
  const token = useSelector((state) => state.token);

  const getPosts = async () => {
    try {
      const response = await fetch("http://localhost:3001/posts", {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        dispatch(setPosts({ posts: data }));
      } else {
        // Handle error response
        // (e.g., display an error message, handle authorization issues)
      }
    } catch (error) {
      console.log("Error fetching posts:", error);
    }
  };

  const getUserPosts = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/posts/${userId}/posts`,
        {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (response.ok) {
        const data = await response.json();
        dispatch(setPosts({ posts: data }));
      } else {
        // Handle error response
        // (e.g., display an error message, handle authorization issues)
      }
    } catch (error) {
      console.log("Error fetching user posts:", error);
    }
  };

  useEffect(() => {
    if (isProfile) {
      getUserPosts();
    } else {
      getPosts();
    }
  }, [userId, isProfile, dispatch]); // Add dispatch as a dependency to ensure effect is updated when dispatch changes

  const handleDeletePost = async (postId) => {
    try {
      const response = await fetch(`http://localhost:3001/posts/${postId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        dispatch(deletePost(postId));
      } else {
        // Handle error response
        // (e.g., display an error message, handle authorization issues)
      }
    } catch (error) {
      console.log("Error deleting post:", error);
    }
  };

  return (
    <>
      {posts.map(
        ({
          _id,
          userId,
          firstName,
          lastName,
          description,
          location,
          picturePath,
          userPicturePath,
          likes,
          comments,
        }) => (
          <PostWidget
            key={_id}
            postId={_id}
            postUserId={userId}
            name={`${firstName} ${lastName}`}
            description={description}
            location={location}
            picturePath={picturePath}
            userPicturePath={userPicturePath}
            likes={likes}
            comments={comments}
            onDelete={() => handleDeletePost(_id)}
          />
        )
      )}
    </>
  );
};

export default PostsWidget;
